import React from 'react'
import { Link } from 'react-router-dom';
import image from '../images/VectoRent.png'

export default function Login(){
    const [formData, setFormData] = React.useState(
        {
            user : " ",
            pass : " "
        }
    )
    // function handleClick(event) {
    //     event.preventDefault(); 
    //     const { name, value } = event.target;
    //     if (formData.user === "Vedant" && formData.pass === "qwerty") {
    //       alert("Login successful!");
    //     } else {
    //       alert("Invalid credentials. Please try again.");
    //     }
    //   }
      function handleChange(event){
        setFormData(prevData=>{
            return{
                ...prevData,
                [event.target.name] : event.target.value
            }
        });
      } 


    return (
        <div className='login-div'>
            <form className='login-form' onSubmit={handleClick}>
                <img className='login-image'  src={image} alt=''></img>
                <div className='form-username'>
                    <label>Username : </label>
                    <input type="text" name="user"  onClick={handleChange} value={formData.user} required />
                </div>
                <div className='form-password'>
                    <label>Password : </label>
                    <input type="password" name="pass" onClick={handleChange} value={formData.pass} required />
                </div>
                <div class="box">
                    <Link to="#" class="btn btn-white btn-animation-1" onClick={handleClick}>Login</Link>
                </div>
            </form>
            <div class="box">
                <Link to="/signup" class="btn btn-white btn-animation-1">Sign Up</Link>
            </div>
        </div>
    )
}
