import React from 'react'
import { Link, useHistory } from 'react-router-dom';
import image from '../images/VectoRent.png'

export default function Login(){
    const [formData, setFormData] = React.useState(
        {
            user : " ",
            pass : " "
        }
    )

    function handleChange(event) {
        const { name, value } = event.target;
        setFormData((prev) => ({
          ...prev,
          [name]: value,
        }));
      }

      const history = useHistory();
      const [validation,setValidation] = React.useState()
      function handleSubmit(event) {
        event.preventDefault();
        if(formData.user === "Vedant" && formData.pass === "qwerty"){
            console.log('login successfull')
            setValidation(true);
            history.push('/home');
        }else{
            console.log("no");
            setValidation(false);
        }
        console.log(formData.user);

      }

    // console.log(formData)
    return (
        <div className='login-div'>
            <form className='login-form'>
                <img className='login-image'  src={image} alt=''></img>
                <div className='form-group'>
                    <label>UserName:</label>
                    <input type="text" name="user" onChange={handleChange} required />
                </div>
                <div className='form-group'>
                    <label>Password:</label>
                    <input type="text" name="pass" onChange={handleChange} required />
                </div>
                <div className="box">
                    <Link to={validation?'/home':'#'} className="btn btn-white btn-animation-1" onClick={handleSubmit}>Login</Link>
                </div>
                {/* {validation? <p>Login successfull</p>:<p>Invalid Username or Password</p>} */}
            </form>
            <div className="box">
                <Link to="/signup" className="btn btn-white btn-animation-1" >Sign Up</Link>
            </div>
        </div>
    )
}
