import React from 'react'
import { Link } from 'react-router-dom';
import image from '../images/VectoRent.png'

export default function Login(){
    const [formData, setFormData] = React.useState(
        {
            username : "Vedant",
            password : "qwerty"
        }
    )
    function handleClick(event) {
        const { name, value } = event.target;
        console.log(formData)
        if(formData.username === value && formData.username === value){
            alert("sg")
        }else{
            alert("re-try")
        }
      }

      function handleChange(event) {
        const { name, value } = event.target;
        setFormData(prev=>{
            return{
                ...prev,
                [name]:value
            }
        })
      }

    return (
        <div className='login-div'>
            <form className='login-form' onSubmit={handleClick}>
                <img className='login-image'  src={image} alt=''></img>
                <div className='form-username'>
                    <label>Username : </label>
                    <input type="text" name="user" onChange={handleChange}  required />
                </div>
                <div className='form-password'>
                    <label>Password : </label>
                    <input type="password" name="pass" onChange={handleChange} required />
                </div>
                <div class="box">
                    <Link to="#" class="btn btn-white btn-animation-1" onClick={handleClick}>Login</Link>
                </div>
            </form>
            <div class="box">
                <Link to="/signup" class="btn btn-white btn-animation-1">Sign Up</Link>
            </div>
        </div>
    )
}
